shared/schema.ts.content:
import { z } from "zod";

// Travel Planning Schemas

export const groupTypeSchema = z.enum(["friends", "family", "solo"]);
export type GroupType = z.infer<typeof groupTypeSchema>;

export const tripScopeSchema = z.enum(["domestic", "international"]);
export type TripScope = z.infer<typeof tripScopeSchema>;

export const destinationSchema = z.object({
  id: z.string(),
  name: z.string(),
  country: z.string(),
  image: z.string(),
  description: z.string(),
  popularActivities: z.array(z.string()),
  scope: tripScopeSchema.optional(),
});
export type Destination = z.infer<typeof destinationSchema>;

export const hotelSchema = z.object({
  id: z.string(),
  name: z.string(),
  destinationId: z.string(),
  image: z.string(),
  rating: z.number().min(1).max(5),
  reviewCount: z.number(),
  price: z.number(),
  features: z.array(z.string()),
  safety: z.number().min(1).max(5),
  comfort: z.number().min(1).max(5),
});
export type Hotel = z.infer<typeof hotelSchema>;

export const layoverSchema = z.object({
  airport: z.string(),
  duration: z.string(),
});

export const flightSchema = z.object({
  id: z.string(),
  airline: z.string(),
  from: z.string(),
  to: z.string(),
  departureTime: z.string(),
  arrivalTime: z.string(),
  duration: z.string(),
  price: z.number(),
  stops: z.number(),
  comfort: z.number().min(1).max(5),
  comfortScore: z.number().int().min(0).max(100).optional(),
  costScore: z.number().int().min(0).max(100).optional(),
  layoverDetails: z.array(layoverSchema).optional(),
});
export type Flight = z.infer<typeof flightSchema>;
export type Layover = z.infer<typeof layoverSchema>;

export const activityPrioritySchema = z.enum(["low", "medium", "high"]);

export const activitySchema = z.object({
  id: z.string(),
  name: z.string(),
  destinationId: z.string(),
  category: z.string(),
  image: z.string(),
  duration: z.string(),
  price: z.number(),
  rating: z.number().min(1).max(5),
  description: z.string(),
  reviewCount: z.number().optional(),
  tags: z.array(z.string()).optional(),
  priority: activityPrioritySchema.optional(),
  bookingWindow: z.number().optional(),
});
export type Activity = z.infer<typeof activitySchema>;
export type ActivityPriority = z.infer<typeof activityPrioritySchema>;

export const referenceItemSchema = z.object({
  id: z.string(),
  image: z.string(),
  source: z.enum(["instagram", "tiktok", "pinterest", "youtube"]),
  title: z.string(),
  url: z.string(),
});
export type ReferenceItem = z.infer<typeof referenceItemSchema>;

export const itineraryActivitySchema = z.object({
  id: z.string(),
  activityId: z.string(),
  day: z.number(),
  startTime: z.string(),
  endTime: z.string(),
  notes: z.string().optional(),
});
export type ItineraryActivity = z.infer<typeof itineraryActivitySchema>;

export const itinerarySchema = z.object({
  id: z.string(),
  name: z.string(),
  destinationId: z.string(),
  hotelId: z.string(),
  flightId: z.string(),
  startDate: z.string(),
  endDate: z.string(),
  activities: z.array(itineraryActivitySchema),
  totalCost: z.number(),
  pros: z.array(z.string()),
  cons: z.array(z.string()),
});
export type Itinerary = z.infer<typeof itinerarySchema>;

export const tripPlanSchema = z.object({
  id: z.string(),
  groupType: groupTypeSchema,
  tripScope: tripScopeSchema.optional(),
  budgetMin: z.number(),
  budgetMax: z.number(),
  startDate: z.string(),
  endDate: z.string(),
  destinationId: z.string(),
  selectedHotelId: z.string().optional(),
  selectedFlightId: z.string().optional(),
  referenceBoard: z.array(referenceItemSchema),
  itineraries: z.array(itinerarySchema),
  selectedItineraryId: z.string().optional(),
});
export type TripPlan = z.infer<typeof tripPlanSchema>;

client/src/components/SwipeCard.tsx.content:
import { useState } from "react";
import { Star, Heart, X, Wifi, Coffee, Shield, MapPin } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";

interface SwipeCardProps {
  id: string;
  name: string;
  image: string;
  rating: number;
  price: number;
  type: "hotel" | "flight" | "activity";
  onSwipe: (id: string, direction: "left" | "right") => void;

  reviewCount?: number;
  features?: string[];

  category?: string;
  duration?: string;
  description?: string;
}

const featureIcons: Record<string, any> = {
  "Free WiFi": Wifi,
  "Breakfast": Coffee,
  "High Safety": Shield,
  "Central Location": MapPin,
};

export function SwipeCard({
  id,
  name,
  image,
  rating,
  price,
  type,
  onSwipe,
  reviewCount,
  features,
  category,
  duration,
  description,
}: SwipeCardProps) {
  const [isDragging, setIsDragging] = useState(false);
  const [offset, setOffset] = useState({ x: 0, y: 0 });
  const [startPos, setStartPos] = useState({ x: 0, y: 0 });

  const handleStart = (clientX: number, clientY: number) => {
    setIsDragging(true);
    setStartPos({ x: clientX, y: clientY });
  };

  const handleMove = (clientX: number, clientY: number) => {
    if (!isDragging) return;
    const deltaX = clientX - startPos.x;
    const deltaY = clientY - startPos.y;
    setOffset({ x: deltaX, y: deltaY });
  };

  const handleEnd = () => {
    if (!isDragging) return;
    setIsDragging(false);

    if (Math.abs(offset.x) > 150) {
      onSwipe(id, offset.x > 0 ? "right" : "left");
    }

    setOffset({ x: 0, y: 0 });
  };

  const handleMouseDown = (e: React.MouseEvent) => {
    handleStart(e.clientX, e.clientY);
  };

  const handleMouseMove = (e: React.MouseEvent) => {
    handleMove(e.clientX, e.clientY);
  };

  const handleTouchStart = (e: React.TouchEvent) => {
    const touch = e.touches[0];
    handleStart(touch.clientX, touch.clientY);
  };

  const handleTouchMove = (e: React.TouchEvent) => {
    const touch = e.touches[0];
    handleMove(touch.clientX, touch.clientY);
  };

  const handleSwipeButton = (direction: "left" | "right") => {
    onSwipe(id, direction);
  };

  const rotation = offset.x * 0.05;
  const opacity = 1 - Math.abs(offset.x) / 500;

  return (
    <div
      className="absolute inset-0 cursor-grab active:cursor-grabbing"
      style={{
        transform: `translateX(${offset.x}px) translateY(${offset.y}px) rotate(${rotation}deg)`,
        opacity,
        transition: isDragging ? "none" : "all 0.3s ease-out",
      }}
      onMouseDown={handleMouseDown}
      onMouseMove={handleMouseMove}
      onMouseUp={handleEnd}
      onMouseLeave={handleEnd}
      onTouchStart={handleTouchStart}
      onTouchMove={handleTouchMove}
      onTouchEnd={handleEnd}
      data-testid={`swipe-card-${id}`}
    >
      <div className="bg-card border border-card-border rounded-xl overflow-hidden h-full shadow-xl">
        <div className="aspect-[4/3] relative overflow-hidden">
          <img
            src={image}
            alt={name}
            className="w-full h-full object-cover"
          />
          <div className="absolute inset-0 bg-gradient-to-t from-black/60 via-black/20 to-transparent" />
          <Badge className="absolute top-4 right-4 bg-primary text-primary-foreground" data-testid={`badge-price-${id}`}>
            ${price}
          </Badge>
        </div>

        <div className="p-6 space-y-4">
          <div className="flex items-start justify-between gap-2">
            <h3 className="text-2xl font-bold text-foreground flex-1" data-testid={`text-name-${id}`}>{name}</h3>
            {category && type === "activity" && (
              <Badge variant="secondary" className="shrink-0" data-testid={`badge-category-${id}`}>
                {category}
              </Badge>
            )}
          </div>

          <div className="flex items-center gap-2">
            <div className="flex items-center gap-1" data-testid={`rating-${id}`}>
              {Array.from({ length: 5 }).map((_, i) => (
                <Star
                  key={i}
                  className={`w-4 h-4 ${
                    i < Math.floor(rating)
                      ? "fill-amber-400 text-amber-400"
                      : "text-border"
                  }`}
                />
              ))}
            </div>
            {reviewCount && (
              <span className="text-sm text-muted-foreground" data-testid={`text-reviews-${id}`}>
                ({reviewCount} reviews)
              </span>
            )}
            {duration && type === "activity" && (
              <span className="text-sm text-muted-foreground" data-testid={`text-duration-${id}`}>
                • {duration}
              </span>
            )}
          </div>

          {description && type === "activity" && (
            <p className="text-sm text-muted-foreground line-clamp-2" data-testid={`text-description-${id}`}>
              {description}
            </p>
          )}

          {features && features.length > 0 && (
            <div className="grid grid-cols-2 gap-3">
              {features.slice(0, 4).map((feature, idx) => {
                const Icon = featureIcons[feature] || Wifi;
                return (
                  <div key={idx} className="flex items-center gap-2 text-sm text-muted-foreground">
                    <Icon className="w-4 h-4" />
                    <span>{feature}</span>
                  </div>
                );
              })}
            </div>
          )}
        </div>

        <div className="absolute bottom-6 left-0 right-0 flex justify-center gap-4 px-6">
          <Button
            size="icon"
            variant="destructive"
            className="w-16 h-16 rounded-full shadow-lg"
            onClick={() => handleSwipeButton("left")}
            data-testid="button-swipe-left"
          >
            <X className="w-8 h-8" />
          </Button>
          <Button
            size="icon"
            className="w-16 h-16 rounded-full shadow-lg bg-primary hover:bg-primary/90"
            onClick={() => handleSwipeButton("right")}
            data-testid="button-swipe-right"
          >
            <Heart className="w-8 h-8" />
          </Button>
        </div>
      </div>
    </div>
  );
}

client/src/components/ui/chart.tsx.content:
"use client"

import * as React from "react"
import * as RechartsPrimitive from "recharts"

import { cn } from "@/lib/utils"

// Format: { THEME_NAME: CSS_SELECTOR }
const THEMES = { light: "", dark: ".dark" } as const

export type ChartConfig = {
  [k in string]: {
    label?: React.ReactNode
    icon?: React.ComponentType
  } & (
    | { color?: string; theme?: never }
    | { color?: never; theme: Record<keyof typeof THEMES, string> }
  )
}

type ChartContextProps = {
  config: ChartConfig
}

const ChartContext = React.createContext<ChartContextProps | null>(null)

function useChart() {
  const context = React.useContext(ChartContext)

  if (!context) {
    throw new Error("useChart must be used within a <ChartContainer />")
  }

  return context
}

const ChartContainer = React.forwardRef<
  HTMLDivElement,
  React.ComponentProps<"div"> & {
    config: ChartConfig
    children: React.ComponentProps<
      typeof RechartsPrimitive.ResponsiveContainer
    >["children"]
  }
>(({ id, className, children, config, ...props }, ref) => {
  const uniqueId = React.useId()
  const chartId = `chart-${id || uniqueId.replace(/:/g, "")}`

  return (
    <ChartContext.Provider value={{ config }}>
      <div
        data-chart={chartId}
        ref={ref}
        className={cn(
          "flex aspect-video justify-center text-xs [&_.recharts-cartesian-axis-tick_text]:fill-muted-foreground [&_.recharts-cartesian-grid_line[stroke='#ccc']]:stroke-border/50 [&_.recharts-curve.recharts-tooltip-cursor]:stroke-border [&_.recharts-dot[stroke='#fff']]:stroke-transparent [&_.recharts-layer]:outline-none [&_.recharts-polar-grid_[stroke='#ccc']]:stroke-border [&_.recharts-radial-bar-background-sector]:fill-muted [&_.recharts-rectangle.recharts-tooltip-cursor]:fill-muted [&_.recharts-reference-line_[stroke='#ccc']]:stroke-border [&_.recharts-sector[stroke='#fff']]:stroke-transparent [&_.recharts-sector]:outline-none [&_.recharts-surface]:outline-none",
          className
        )}
        {...props}
      >
        <ChartStyle id={chartId} config={config} />
        <RechartsPrimitive.ResponsiveContainer>
          {children}
        </RechartsPrimitive.ResponsiveContainer>
      </div>
    </ChartContext.Provider>
  )
})
ChartContainer.displayName = "Chart"

const ChartStyle = ({ id, config }: { id: string; config: ChartConfig }) => {
  const colorConfig = Object.entries(config).filter(
    ([, config]) => config.theme || config.color
  )

  if (!colorConfig.length) {
    return null
  }

  return (
    <style
      dangerouslySetInnerHTML={{
        __html: Object.entries(THEMES)
          .map(
            ([theme, prefix]) => `
${prefix} [data-chart=${id}] {
${colorConfig
  .map(([key, itemConfig]) => {
    const color =
      itemConfig.theme?.[theme as keyof typeof itemConfig.theme] ||
      itemConfig.color
    return color ? `  --color-${key}: ${color};` : null
  })
  .join("\n")}
}
`
          )
          .join("\n"),
      }}
    />
  )
}

const ChartTooltip = RechartsPrimitive.Tooltip

const ChartTooltipContent = React.forwardRef<
  HTMLDivElement,
  React.ComponentProps<typeof RechartsPrimitive.Tooltip> &
    React.ComponentProps<"div"> & {
      hideLabel?: boolean
      hideIndicator?: boolean
      indicator?: "line" | "dot" | "dashed"
      nameKey?: string
      labelKey?: string
    }
>(
  (
    {
      active,
      payload,
      className,
      indicator = "dot",
      hideLabel = false,
      hideIndicator = false,
      label,
      labelFormatter,
      labelClassName,
      formatter,
      color,
      nameKey,
      labelKey,
    },
    ref
  ) => {
    const { config } = useChart()

    const tooltipLabel = React.useMemo(() => {
      if (hideLabel || !payload?.length) {
        return null
      }

      const [item] = payload
      const key = `${labelKey || item?.dataKey || item?.name || "value"}`
      const itemConfig = getPayloadConfigFromPayload(config, item, key)
      const value =
        !labelKey && typeof label === "string"
          ? config[label as keyof typeof config]?.label || label
          : itemConfig?.label

      if (labelFormatter) {
        return (
          <div className={cn("font-medium", labelClassName)}>
            {labelFormatter(value, payload)}
          </div>
        )
      }

      if (!value) {
        return null
      }

      return <div className={cn("font-medium", labelClassName)}>{value}</div>
    }, [
      label,
      labelFormatter,
      payload,
      hideLabel,
      labelClassName,
      config,
      labelKey,
    ])

    if (!active || !payload?.length) {
      return null
    }

    const nestLabel = payload.length === 1 && indicator !== "dot"

    return (
      <div
        ref={ref}
        className={cn(
          "grid min-w-[8rem] items-start gap-1.5 rounded-lg border border-border/50 bg-background px-2.5 py-1.5 text-xs shadow-xl",
          className
        )}
      >
        {!nestLabel ? tooltipLabel : null}
        <div className="grid gap-1.5">
          {payload.map((item, index) => {
            const key = `${nameKey || item.name || item.dataKey || "value"}`
            const itemConfig = getPayloadConfigFromPayload(config, item, key)
            const indicatorColor = color || item.payload.fill || item.color

            return (
              <div
                key={item.dataKey}
                className={cn(
                  "flex w-full flex-wrap items-stretch gap-2 [&>svg]:h-2.5 [&>svg]:w-2.5 [&>svg]:text-muted-foreground",
                  indicator === "dot" && "items-center"
                )}
              >
                {formatter && item?.value !== undefined && item.name ? (
                  formatter(item.value, item.name, item, index, item.payload)
                ) : (
                  <>
                    {itemConfig?.icon ? (
                      <itemConfig.icon />
                    ) : (
                      !hideIndicator && (
                        <div
                          className={cn(
                            "shrink-0 rounded-[2px] border-[--color-border] bg-[--color-bg]",
                            {
                              "h-2.5 w-2.5": indicator === "dot",
                              "w-1": indicator === "line",
                              "w-0 border-[1.5px] border-dashed bg-transparent":
                                indicator === "dashed",
                              "my-0.5": nestLabel && indicator === "dashed",
                            }
                          )}
                          style={
                            {
                              "--color-bg": indicatorColor,
                              "--color-border": indicatorColor,
                            } as React.CSSProperties
                          }
                        />
                      )
                    )}
                    <div
                      className={cn(
                        "flex flex-1 justify-between leading-none",
                        nestLabel ? "items-end" : "items-center"
                      )}
                    >
                      <div className="grid gap-1.5">
                        {nestLabel ? tooltipLabel : null}
                        <span className="text-muted-foreground">
                          {itemConfig?.label || item.name}
                        </span>
                      </div>
                      {item.value && (
                        <span className="font-mono font-medium tabular-nums text-foreground">
                          {item.value.toLocaleString()}
                        </span>
                      )}
                    </div>
                  </>
                )}
              </div>
            )
          })}
        </div>
      </div>
    )
  }
)
ChartTooltipContent.displayName = "ChartTooltip"

const ChartLegend = RechartsPrimitive.Legend

const ChartLegendContent = React.forwardRef<
  HTMLDivElement,
  React.ComponentProps<"div"> &
    Pick<RechartsPrimitive.LegendProps, "payload" | "verticalAlign"> & {
      hideIcon?: boolean
      nameKey?: string
    }
>(
  (
    { className, hideIcon = false, payload, verticalAlign = "bottom", nameKey },
    ref
  ) => {
    const { config } = useChart()

    if (!payload?.length) {
      return null
    }

    return (
      <div
        ref={ref}
        className={cn(
          "flex items-center justify-center gap-4",
          verticalAlign === "top" ? "pb-3" : "pt-3",
          className
        )}
      >
        {payload.map((item) => {
          const key = `${nameKey || item.dataKey || "value"}`
          const itemConfig = getPayloadConfigFromPayload(config, item, key)

          return (
            <div
              key={item.value}
              className={cn(
                "flex items-center gap-1.5 [&>svg]:h-3 [&>svg]:w-3 [&>svg]:text-muted-foreground"
              )}
            >
              {itemConfig?.icon && !hideIcon ? (
                <itemConfig.icon />
              ) : (
                <div
                  className="h-2 w-2 shrink-0 rounded-[2px]"
                  style={{
                    backgroundColor: item.color,
                  }}
                />
              )}
              {itemConfig?.label}
            </div>
          )
        })}
      </div>
    )
  }
)
ChartLegendContent.displayName = "ChartLegend"

// Helper to extract item config from a payload.
function getPayloadConfigFromPayload(
  config: ChartConfig,
  payload: unknown,
  key: string
) {
  if (typeof payload !== "object" || payload === null) {
    return undefined
  }

  const payloadPayload =
    "payload" in payload &&
    typeof payload.payload === "object" &&
    payload.payload !== null
      ? payload.payload
      : undefined

  let configLabelKey: string = key

  if (
    key in payload &&
    typeof payload[key as keyof typeof payload] === "string"
  ) {
    configLabelKey = payload[key as keyof typeof payload] as string
  } else if (
    payloadPayload &&
    key in payloadPayload &&
    typeof payloadPayload[key as keyof typeof payloadPayload] === "string"
  ) {
    configLabelKey = payloadPayload[
      key as keyof typeof payloadPayload
    ] as string
  }

  return configLabelKey in config
    ? config[configLabelKey]
    : config[key as keyof typeof config]
}

export {
  ChartContainer,
  ChartTooltip,
  ChartTooltipContent,
  ChartLegend,
  ChartLegendContent,
  ChartStyle,
}

